function solve(input) {
    const lines = input.trim().split('\n');
    const price = lines[0].split(/\s+/).map(Number);
    const n = price.length;
    const special = [];
    let i = 1;
    while (i < lines.length && lines[i].split(/\s+/).length === n + 1) {
        special.push(lines[i].split(/\s+/).map(Number));
        i++;
    }
    const needs = lines[i].split(/\s+/).map(Number);
    const sol = new Solution();
    console.log(sol.shoppingOffers(price, special, needs));
}

