function solve(input) {
    const lines = input.trim().split('\n');
    const [V, E] = lines[0].split(' ').map(Number);
    const edges = [];
    for (let i = 0; i < E; i++) {
        edges.push(lines[i+1].split(' ').map(Number));
    }
    const S = parseInt(lines[E+1]);
    const sol = new Solution();
    const res = sol.bellman_ford(V, edges, S);
    console.log(res.join(' '));
}

