        int V = Integer.parseInt(firstLine[0]);
        int E = Integer.parseInt(firstLine[1]);
        ArrayList<ArrayList<ArrayList<Integer>>> adj = new ArrayList<>();
        for (int i = 0; i < V; i++) adj.add(new ArrayList<>());
