            String[] parts = br.readLine().trim().split(" ");
            int u = Integer.parseInt(parts[0]);
            int v = Integer.parseInt(parts[1]);
            int w = Integer.parseInt(parts[2]);
            ArrayList<Integer> t1 = new ArrayList<>(Arrays.asList(v, w));
            adj.get(u).add(t1);
            ArrayList<Integer> t2 = new ArrayList<>(Arrays.asList(u, w));
            adj.get(v).add(t2);
        }
        Solution sol = new Solution();
        System.out.println(sol.spanningTree(V, adj));
    }
