function solve(input) {
    const lines = input.trim().split('\n');
    const [m, n] = lines[0].split(' ').map(Number);
    const grid = [];
    for (let i = 0; i < m; i++) {
        grid.push(lines[i+1].split(' '));
    }
    const sol = new Solution();
    console.log(sol.numIslands(grid));
