void swap(long long* a, long long* b) { long long t = *a; *a = *b; *b = t; }

void heapify(long long heap[], int n, int i) {
    int smallest = i, left = 2*i+1, right = 2*i+2;
    if (left < n && heap[left] < heap[smallest]) smallest = left;
    if (right < n && heap[right] < heap[smallest]) smallest = right;
    if (smallest != i) { swap(&heap[i], &heap[smallest]); heapify(heap, n, smallest); }
}

long long extractMin(long long heap[], int* n) {
    long long min = heap[0];
    heap[0] = heap[--(*n)];
    heapify(heap, *n, 0);
    return min;
}

void insertHeap(long long heap[], int* n, long long val) {
    heap[(*n)++] = val;
    int i = *n - 1;
    while (i > 0 && heap[(i-1)/2] > heap[i]) {
        swap(&heap[(i-1)/2], &heap[i]);
        i = (i-1)/2;
    }
}

