function solve(input) {
    const lines = input.trim().split('\n');
    const n = parseInt(lines[0]);
    const jobs = [];
    for (let i = 1; i <= n; i++) {
        const [d, p] = lines[i].split(' ').map(Number);
        jobs.push([d, p]);
    }
    const sol = new Solution();
    console.log(sol.jobSequencing(jobs));
}

