    const strs = parts.slice(1);
    const sol = new Solution();
    const res = sol.groupAnagrams(strs);
    // Sort for output verification
    res.forEach(group => group.sort());
    res.sort((a, b) => {
        if (a.length !== b.length) return a.length - b.length;
        return a[0].localeCompare(b[0]);
    });
    res.forEach(group => console.log(group.join(' ')));
