function solve(input){
  const lines = input.trim().split("\n");
  const n = parseInt(lines[0]);
  const points = [];
  for(let i=1;i<=n;i++){
    points.push(lines[i].split(" ").map(Number));
  }
  const sol = new Solution();
  const result = sol.isReflected(points);
  console.log(result ? "true" : "false");
}
