class Solution {
    public int countTriplets(int n, int s) {
        int count = 0;
        for (int i = 1; i <= n; i++) {
            for (int j = 1; j <= n; j++) {
                for (int k = 1; k <= n; k++) {
                    if (i + j + k == s) {
                        count++;
                    }
                }
            }
        }
        return count;
    }

    public static void main(String[] args) throws Exception {
        Scanner sc = new Scanner(System.in);
        if(!sc.hasNextInt()) return;
        int n = sc.nextInt();
        int s = sc.nextInt();
        Solution sol = new Solution();
        System.out.println(sol.countTriplets(n, s));
    }
}

