class Solution {
    public void printBlocks(int n) {
        for (int k = 1; k <= n; k++) {
            for (int i = 0; i < n; i++) {
                for (int j = 0; j < n; j++) {
                    System.out.print(k + (j == n - 1 ? "" : " "));
                }
                System.out.println();
            }
            if (k < n) {
                System.out.println();
            }
        }
    }

    public static void main(String[] args) throws Exception {
        Scanner sc = new Scanner(System.in);
        if(!sc.hasNextInt()) return;
        int n = sc.nextInt();
        Solution sol = new Solution();
        sol.printBlocks(n);
    }
}

