import java.io.*;
import java.util.*;

class Solution {
    public void printLayers(int n) {
        for (int L = 1; L <= n; L++) {
            for (int i = 0; i < L; i++) {
                for (int j = 0; j < L; j++) {
                    System.out.print(L + (j == L - 1 ? "" : " "));
                }
                System.out.println();
            }
            if (L < n) {
                System.out.println();
            }
        }
    }

    public static void main(String[] args) throws Exception {
        Scanner sc = new Scanner(System.in);
        if(!sc.hasNextInt()) return;
        int n = sc.nextInt();
        Solution sol = new Solution();
        sol.printLayers(n);
    }
}

