#include <stdio.h>

void printLayers(int n) {
    for (int L = 1; L <= n; L++) {
        for (int i = 0; i < L; i++) {
            for (int j = 0; j < L; j++) {
                printf("%d%s", L, (j == L - 1 ? "" : " "));
            }
