class Solution {
    public int countProductTriplets(int n, int p) {
        int count = 0;
        for (int i = 1; i <= n; i++) {
            for (int j = 1; j <= n; j++) {
                for (int k = 1; k <= n; k++) {
                    if (i * j * k == p) {
                        count++;
                    }
                }
            }
        }
        return count;
    }

    public static void main(String[] args) throws Exception {
        Scanner sc = new Scanner(System.in);
        if(!sc.hasNextInt()) return;
        int n = sc.nextInt();
        int p = sc.nextInt();
        Solution sol = new Solution();
        System.out.println(sol.countProductTriplets(n, p));
    }
}

