class Solution {
    public long calculateSum(int x, int y, int z) {
        long total = 0;
        for (int i = 0; i <= x; i++) {
            for (int j = 0; j <= y; j++) {
                for (int k = 0; k <= z; k++) {
                    total += (i + j + k);
                }
            }
        }
        return total;
    }

    public static void main(String[] args) throws Exception {
        Scanner sc = new Scanner(System.in);
        if(!sc.hasNextInt()) return;
        int x = sc.nextInt();
        int y = sc.nextInt();
        int z = sc.nextInt();
        Solution sol = new Solution();
        System.out.println(sol.calculateSum(x, y, z));
    }
}

