class Solution {
    public int countPerfectSquareTriplets(int n) {
        int count = 0;
        for (int i = 1; i <= n; i++) {
            for (int j = i; j <= n; j++) {
                for (int k = j; k <= n; k++) {
                    long s = (long)i*i + (long)j*j + (long)k*k;
                    long root = (long)Math.sqrt(s);
                    if (root * root == s) {
                        count++;
                    }
                }
            }
        }
        return count;
    }

