class Solution {
    public int countXorSumTriplets(int n, int x) {
        int count = 0;
        for (int i = 1; i <= n; i++) {
            for (int j = 1; j <= n; j++) {
                for (int k = 1; k <= n; k++) {
                    if ((i ^ j ^ k) == (i + j + k - x)) {
                        count++;
                    }
                }
            }
        }
        return count;
    }

    public static void main(String[] args) throws Exception {
        Scanner sc = new Scanner(System.in);
        if(!sc.hasNextInt()) return;
        int n = sc.nextInt();
        int x = sc.nextInt();
        Solution sol = new Solution();
        System.out.println(sol.countXorSumTriplets(n, x));
    }
}

