import java.io.*;
import java.util.*;

class Solution {
    public void multiplyMatrices(int n, int[][] A, int[][] B) {
        int[][] res = new int[n][n];
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                for (int k = 0; k < n; k++) {
                    res[i][j] += A[i][k] * B[k][j];
                }
            }
        }
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                System.out.print(res[i][j] + (j == n - 1 ? "" : " "));
            }
            System.out.println();
        }
    }

    public static void main(String[] args) throws Exception {
        Scanner sc = new Scanner(System.in);
        if(!sc.hasNextInt()) return;
        int n = sc.nextInt();
        int[][] A = new int[n][n];
        int[][] B = new int[n][n];
        for(int i=0; i<n; i++) for(int j=0; j<n; j++) A[i][j] = sc.nextInt();
        for(int i=0; i<n; i++) for(int j=0; j<n; j++) B[i][j] = sc.nextInt();
        Solution sol = new Solution();
        sol.multiplyMatrices(n, A, B);
    }
}

