function solve(input) {
    const parts = input.trim().split(/\s+/).map(Number);
    if (parts.length === 0) return;
    const n = parts[0];
    let idx = 1;
    const A = [];
    for (let i = 0; i < n; i++) {
        A.push(parts.slice(idx, idx + n));
        idx += n;
    }
    const B = [];
    for (let i = 0; i < n; i++) {
        B.push(parts.slice(idx, idx + n));
        idx += n;
    }
    const sol = new Solution();
    sol.multiplyMatrices(n, A, B);
}

