class Solution:
    def countPairwiseCoprime(self, n):
        count = 0
        for i in range(1, n + 1):
            for j in range(i + 1, n + 1):
                if math.gcd(i, j) != 1: continue
                for k in range(j + 1, n + 1):
                    if math.gcd(i, k) == 1 and math.gcd(j, k) == 1:
                        count += 1
        return count

