        int count = 0;
        for (int i = 1; i <= n; i++) {
            for (int j = i + 1; j <= n; j++) {
                if (gcd(i, j) != 1) continue;
                for (int k = j + 1; k <= n; k++) {
                    if (gcd(i, k) == 1 && gcd(j, k) == 1) {
                        count++;
                    }
                }
            }
        }
        return count;
    }

