import java.util.*;
class Solution {
    public void solve(String IP) {
        if (isIPv4(IP)) System.out.println("IPv4");
        else if (isIPv6(IP)) System.out.println("IPv6");
        else System.out.println("Neither");
    }
    private boolean isIPv4(String s) {
        String[] parts = s.split("\\.", -1);
        if (parts.length != 4) return false;
        for (String p : parts) {
            try {
                if (p.length() == 0 || p.length() > 3) return false;
                if (p.charAt(0) == '0' && p.length() > 1) return false;
                int val = Integer.parseInt(p);
                if (val < 0 || val > 255) return false;
            } catch (NumberFormatException e) { return false; }
        }
        return true;
    }
    private boolean isIPv6(String s) {
        String[] parts = s.split(":", -1);
        if (parts.length != 8) return false;
        for (String p : parts) {
            if (p.length() == 0 || p.length() > 4) return false;
            for (char c : p.toCharArray()) {
                if (Character.digit(c, 16) == -1) return false;
            }
        }
        return true;
    }
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        if (sc.hasNextLine()) new Solution().solve(sc.nextLine().trim());
    }
}

