            const parts = s.split('.');
            if (parts.length !== 4) return false;
            for (const p of parts) {
                if (!/^\d+$/.test(p) || parseInt(p) > 255 || (p[0] === '0' && p.length > 1)) return false;
            }
            return true;
        };
        const isIPv6 = (s) => {
            const parts = s.split(':');
            if (parts.length !== 8) return false;
            for (const p of parts) {
                if (p.length === 0 || p.length > 4 || !/^[0-9a-fA-F]+$/.test(p)) return false;
            }
            return true;
        };
        if (isIPv4(IP)) console.log("IPv4");
        else if (isIPv6(IP)) console.log("IPv6");
        else console.log("Neither");
    }
