import java.util.*;
class Solution {
    public void solve(String input) {
        String[] lines = input.split("\n");
        if (lines.length < 2) return;
        String[] firstLine = lines[0].split(" ");
        int maxWidth = Integer.parseInt(firstLine[1]);
        String[] words = lines[1].split(" ");

        List<String> res = new ArrayList<>();
        int index = 0;
        while (index < words.length) {
            int count = words[index].length();
            int last = index + 1;
            while (last < words.length) {
                if (words[last].length() + count + 1 > maxWidth) break;
                count += words[last].length() + 1;
                last++;
            }
            StringBuilder sb = new StringBuilder();
            int diff = last - index - 1;
            if (last == words.length || diff == 0) {
                for (int i = index; i < last; i++) {
                    sb.append(words[i]);
                    if (i < last - 1) sb.append(" ");
                }
                for (int i = sb.length(); i < maxWidth; i++) sb.append(" ");
            } else {
                int spaces = (maxWidth - count) / diff;
                int r = (maxWidth - count) % diff;
                for (int i = index; i < last; i++) {
                    sb.append(words[i]);
                    if (i < last - 1) {
                        for (int j = 0; j <= (spaces + (i - index < r ? 1 : 0)); j++) sb.append(" ");
                    }
                }
            }
            res.add(sb.toString());
            index = last;
        }
        for (int i = 0; i < res.size(); i++) {
            System.out.print(res.get(i) + (i == res.size() - 1 ? "" : "\n"));
        }
    }
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        StringBuilder sb = new StringBuilder();
        while(sc.hasNextLine()) sb.append(sc.nextLine()).append("\n");
        new Solution().solve(sb.toString().trim());
    }
}

class Solution {
    solve(input) {
        const lines = input.split('\n');
        const [n, maxWidth] = lines[0].split(' ').map(Number);
        const words = lines[1].split(' ');

        let res = [], cur = [], num_of_letters = 0;
        for (let w of words) {
            if (num_of_letters + w.length + cur.length > maxWidth) {
                for (let i = 0; i < maxWidth - num_of_letters; i++) {
                    cur[i % (cur.length - 1 || 1)] += ' ';
                }
                res.push(cur.join(''));
                cur = []; num_of_letters = 0;
            }
            cur.push(w);
            num_of_letters += w.length;
        }
        res.push(cur.join(' ').padEnd(maxWidth));
        console.log(res.join('\n'));
    }
}
