import java.util.*;
class Solution {
    public void solve(String s) {
        Map<Character, Integer> d = new HashMap<>();
        d.put('I', 1); d.put('V', 5); d.put('X', 10); d.put('L', 50);
        d.put('C', 100); d.put('D', 500); d.put('M', 1000);
        int res = 0;
        for (int i = 0; i < s.length(); i++) {
            if (i + 1 < s.length() && d.get(s.charAt(i)) < d.get(s.charAt(i+1))) {
                res -= d.get(s.charAt(i));
            } else {
                res += d.get(s.charAt(i));
            }
        }
        System.out.println(res);
    }
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        if (sc.hasNextLine()) new Solution().solve(sc.nextLine().trim());
    }
}

