function solveProblem(input) {
    const lines = input.trim().split('\n');
    const n = parseInt(lines[0]);
    const equations = [];
    for (let i = 1; i <= n; i++) {
        equations.push(lines[i]);
    }

    const sol = new Solution();
    const result = sol.equationsPossible(equations);
    console.log(result ? 'true' : 'false');
}

